sql.insert:
INSERT INTO Company (
Code,
Name,
[Order],
CreateTime

  Description

) VALUES (
@Code,
@Name,
@Order,
@CreateTime

  @Description

)

sql.get-page-list:
select * from( select *,row_number() over(order by Id desc) uid,(select count(1) from [Company]) total from [Company]
Name like '%'+@Name+'%'
) as tbl where uid between (@PageIndex-1)*@PageSize+1 and @PageIndex*@PageSize
