sql.insert:
INSERT INTO Role (
Name,
CreateTime

  Description

) VALUES (
@Name,
@CreateTime

  @Description

)

sql.get-page-list:
select * from( select *,row_number() over(order by Id desc) uid,(select count(1) from [Role]) total from [Role]
Name like '%'+@Name+'%'
) as tbl where uid between (@PageIndex-1)*@PageSize+1 and @PageIndex*@PageSize
